import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

function Beaches() {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get("http://localhost:5000/experience/categories")
      .then((response) => {
        setCategories(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  useEffect(() => {
    if (selectedCategory) {
      fetchBeachPosts(selectedCategory);
    }
  }, [selectedCategory]);

  const fetchBeachPosts = (category) => {
    setLoading(true);
    axios
      .get(`http://localhost:5000/experience/beaches?category=${category}`)
      .then((response) => {
        const beachPosts = response.data;
        setPosts(beachPosts);
        setLoading(false);
      })
      .catch((error) => {
        console.log(error);
        setLoading(false);
      });
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div className="beaches-container">
      <h2>Beaches Categories</h2>
      <div className="categories">
        {categories.map((category) => (
          <button
            key={category}
            onClick={() => handleCategoryChange(category)}
          >
            {category}
          </button>
        ))}
      </div>
      <h2>Beaches Posts</h2>
      <div className="post-container">
        {posts.map((post) => (
          <div className="post" key={post.ID_post}>
            <div className="post-image">
              <Link to={`/post/${post.ID_post}`}>
                <img src={post.image_url} alt={post.title} />
              </Link>
              <div className="post-details">
                <h2>{post.title}</h2>
                <p>{post.description}</p>
                <Link to="/add" className="add-button">
        Add Post
      </Link>
                <span>{post.date}</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    
    </div>
  );
}

export default Beaches;
