import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

function Beaches() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true); // Initialize loading as true

  useEffect(() => {
    axios
      .get("http://localhost:5000/experience/beaches")
      .then((response) => {
        const beachPosts = response.data.filter(
          (post) => post.category === "beaches"
        );
        setPosts(beachPosts); // Set the beachPosts state
        setLoading(false); // Update loading state when data is loaded
      })
      window.loa
      .catch((error) => {
        console.log(error);
        setLoading(false); // Handle errors and still update loading state
      });
  }, []);

  if (loading) {
    return <p>Loading...</p>; // Show a loading message while data is loading
  }

  return (
    <div className="post-container">
      {posts.map((post) => (
        <div className="post" key={post.ID_post}>
          <div className="post-image">
            <Link to={`/post/${post.ID_post}`}>
              <img src={post.image_url} alt={post.title} />
            </Link>
            <div className="post-details">
              <h2>{post.title}</h2>
              <p>{post.description}</p>
              <span>{post.date}</span>
            </div>
          </div>
        </div>
      ))}
      <Link to="/add" className="add-button">
        Add Post
      </Link>
    </div>
  );
}

export default Beaches;
